import { UseDispatch,useSelector } from 'react-redux'

import {incrAction,decrAction}from './Redux/Product'

 Product =()=>{
  let Prod_object=useSelector((state)=>{
    return state
  })
  
  let incrHandler=()=>{
    useDispatch(incrAction())
  }
  let decrHandler=()=>{
    dispatch(decrAction())
  }
  return<div>
    <button onClick= {incrHandler}>plus</button>
    <button onClick={decrHandler}>minus</button>
  </div>
}
export default Product
