class Message extends Component {
    state;
    constructor(props){
        super(props)
        console.log("First Const")
        this.state={
            msg:"Hello"
        }
    }
    updateHandler=(value)=>{
      this.setState({msg:value})
    }
    
    
    
    render(){
        console.log("Second Render")
        return(
            <div>
                <h2>Message Component</h2>
                <hr/>
                <h3>Value:{this.state.msg}</h3>
                <button onClick={this.updateHandler.bind(this,"Good Norning")}>GM</button>
                <button onClick={this.updateHandler.bind(this,)}>GA</button>
                <button onClick={this.geHandler}>GE</button>
                <button onClick={this.gnHandler}>GN</button>
            </div>
        )
    }
}
export default Message
