import React from 'react'
class Login extends React.Component{
    state={
        email:"",
        password:""

    }
    updateHandler=(event)=>{
        this.setState({
            [event.target.name]:event.target.value
        })
        submitHandler=(event)=>{
            event.preventDefault()

            }
        render();{
                return 
                    <div className='container mt-5'>
                        <pre>{JSON.stringify(this.state)}</pre>
                        <div className='row'>
                            <div className='col-md-5'>
                                <div className='card'>
                                    <div className='card-header'>
                                        <form>
                                            <div className='form-group'>
                                                <input type ="text" className="form-control" name="email" placeholder='email id' onChange={this.updateHandler}/>
                                                <input type ="text" className='form-controle' name="password" placeholder='password' onChange={this.updateHandler}/>
                                                <input type = "submit" className='btn btn-success' value="Login" />
                                            </div>
                                        </form>
                                    </div>
                                </div>
                            </div>
                        </div>

                    </div>
                
            }
    }
 }


export default Login
