import { UseDispatch,useDispatch,useSelector } from 'react-redux'

import {incrAction,decrAction}from './Redux/Product'

const Product =()=>{
  let dispatch=useDispatch()
  let Prod_object=useSelector((state)=>{
    return state
  })
  
  let incrHandler=()=>{
    dispatch(incrAction())
  }
  let decrHandler=()=>{
    dispatch(decrAction())
  }
  return<div>
    <h1>Product</h1>
    <button onClick= {incrHandler}>plus</button>
    <button onClick={decrHandler}>minus</button>
  </div>
}
export default Product
